mapper.select:
select count(1) from person
where del = 0
     AND login_id=#{loginId}
     AND org_id=#{orgId}
     AND person_type=#{userType}
and del = 0

select * from person
where
del = 0
     AND login_id=#{loginId}
     AND org_id=#{orgId}
     AND person_type=#{userType}
limit #{start} , #{pageSize}

select count(1) from person t1 inner join org t2 on t1.org_id=t2.id and t1.del = 0 AND login_id=#{loginId} AND t2.code = #{orgCode} AND org_id=#{orgId} AND t1.modifydatetime > #{modifyTateTime} AND person_type=#{userType} AND sex=#{sex}
AND org_id IN
#{item}
inner join login t3 on t3.id = t1.login_id WHERE not EXISTS ( SELECT t1.uid FROM user_role t1 LEFT JOIN dict t2 ON t1.role_id = t2.id WHERE t2. CODE = 'ROLE_SHCOOLADMIN' AND uid = t1.id )
AND (t1.realname like CONCAT('%',#{realName},'%') or t3.loginName1 like CONCAT('%',#{realName},'%') or t3.loginName2 like CONCAT('%',#{realName},'%') or t3.loginName3 like CONCAT('%',#{realName},'%') or t3.loginName4 like CONCAT('%',#{realName},'%') )

select t1.*, IFNULL(t2.del,1) as disable, t2.loginName1 as loginName, t3.name as orgName, t3.code as orgCode, t3.org_type as orgType from person t1 inner join org t3 on t1.org_id = t3.id and t1.del = 0 AND t1.login_id=#{loginId} AND t3.code = #{orgCode} AND t1.org_id=#{orgId} AND t1.modifydatetime > #{modifyTateTime} AND t1.person_type=#{userType} AND t1.sex=#{sex}
AND t1.org_id IN
#{item}
inner join login t2 on t1.login_id = t2.id WHERE not EXISTS ( SELECT t1.uid FROM user_role t1 LEFT JOIN dict t2 ON t1.role_id = t2.id WHERE t2. CODE = 'ROLE_SHCOOLADMIN' AND uid = t1.id )
AND (t1.realname like CONCAT('%',#{realName},'%') or t2.loginName1 like CONCAT('%',#{realName},'%') or t2.loginName2 like CONCAT('%',#{realName},'%') or t2.loginName3 like CONCAT('%',#{realName},'%') or t2.loginName4 like CONCAT('%',#{realName},'%') )
limit #{start} , #{pageSize}

select count(1) FROM (SELECT c.teacher id, c.id as class_id,c.org_id,c.start_year,c.dict_code,c.class_number, c.del,c.createdatetime,c.modifydatetime,CONCAT(SUBSTRING(c.class_number,6,2) ,'班') AS class_name,d.NAME AS grade_name, p.realname,p.tel,p.email,p.identity_no, p.pic,p.sex,org.code as orgCode FROM class c INNER JOIN org org ON org.id = c.org_id LEFT JOIN section_grade sg ON c.dict_code = sg.section_dict AND #{schoolYear}-c.start_year = sg.sort LEFT JOIN dict d ON sg.grade_dict = d.`code` left join person p on c.teacher = p.id WHERE c.del = 0 and c.teacher != ""
and c.id = #{classId}
and c.id in
#{item}
AND ((c.dict_code = 'XD_XIAOXUE' and #{schoolYear}-c.start_year < 6) OR (c.dict_code = 'XD_CHUZHONG' and #{schoolYear}-c.start_year < 3) OR (c.dict_code = 'XD_YOUERYUAN' and #{schoolYear}-c.start_year < 3) OR (c.dict_code = 'XD_GAOZHONG' and #{schoolYear}-c.start_year < 3)) AND p.realname like CONCAT('%',#{realName},'%') ) AS cc LEFT JOIN dict dd ON cc.dict_code = dd.`code` where
cc.org_id = #{orgId}
cc.orgCode = #{orgCode}
and cc.id!=""

select cc.* ,dd.NAME AS section_name FROM (SELECT c.teacher id, c.id as class_id,c.org_id,c.start_year,c.dict_code,c.class_number, c.del,c.createdatetime,c.modifydatetime,CONCAT(SUBSTRING(c.class_number,6,2) ,'班') AS class_name,d.NAME AS grade_name, p.realname,p.tel,p.email,p.identity_no, p.pic,p.sex,org.code as orgCode FROM class c INNER JOIN org org ON org.id = c.org_id LEFT JOIN section_grade sg ON c.dict_code = sg.section_dict AND #{schoolYear}-c.start_year = sg.sort LEFT JOIN dict d ON sg.grade_dict = d.`code` left join person p on c.teacher = p.id WHERE c.del = 0 and c.teacher != ""
and c.id = #{classId}
and c.id in
#{item}
AND ((c.dict_code = 'XD_XIAOXUE' and #{schoolYear}-c.start_year < 6) OR (c.dict_code = 'XD_CHUZHONG' and #{schoolYear}-c.start_year < 3) OR (c.dict_code = 'XD_YOUERYUAN' and #{schoolYear}-c.start_year < 3) OR (c.dict_code = 'XD_GAOZHONG' and #{schoolYear}-c.start_year < 3)) AND p.realname like CONCAT('%',#{realName},'%') ) AS cc LEFT JOIN dict dd ON cc.dict_code = dd.`code` where
cc.org_id = #{orgId}
cc.orgCode = #{orgCode}
and cc.id!="" ORDER BY start_year desc ,cc.class_number asc
limit #{start} , #{pageSize}

select
  count(1)
from person t1 inner join org t3
on t1.org_id = t3.id and t1.del = 0
 AND t1.login_id=#{loginId}
 AND t1.org_id=#{orgId}
 AND t1.person_type=#{userType}
 AND t1.sex=#{sex}
AND t1.realname like CONCAT('%',#{realName},'%')

    AND t1.org_id IN

        #{item}


left join login t2 on t1.login_id = t2.id

    and (t2.loginName1=#{loginName} or t2.loginName2=#{loginName} or t2.loginName3=#{loginName} or t2.loginName4 =#{loginName})


    where
    t2.id is not null

select
t1.*,
IFNULL(t2.del,1) as disable,
t2.loginName1 as loginName,
t3.name as orgName
from person t1 inner join org t3
on t1.org_id = t3.id and t1.del = 0
 AND t1.login_id=#{loginId}
 AND t1.org_id=#{orgId}
 AND t1.person_type=#{userType}
 AND t1.sex=#{sex}
AND t1.realname like CONCAT('%',#{realName},'%')

    AND t1.org_id IN

        #{item}


left join login t2 on t1.login_id = t2.id

    and (t2.loginName1=#{loginName} or t2.loginName2=#{loginName} or t2.loginName3=#{loginName} or t2.loginName4 =#{loginName})


    where
    t2.id is not null


    limit #{start} , #{pageSize}

select count(1) from person t1
inner join org org
on t1.org_id = org.id
where

     t1.del = 0
     AND t1.login_id=#{loginId}
     AND org.code = #{orgCode}
     AND t1.org_id=#{orgId}
     AND t1.person_type=#{userType}
    AND t1.modifydatetime > #{modifyTateTime}
    AND t1.realname like CONCAT('%',#{realName},'%')
    AND t1.class_number =#{classNumber}
    AND t1.sex =#{sex}

        AND t1.org_id IN

            #{item}

SELECT t1.id, t1.login_id, t1.person_type, t1.org_id, t1.tel, IFNULL(t1.number,'') AS number, IFNULL(t1.email,'') AS email, IFNULL(t1.identity_no,'') AS identity_no, IFNULL(t1.pic,'')as pic, t1.del, t1.createdatetime, t1.modifydatetime, IFNULL(t1.card_no, '')as card_no, IFNULL(t1.class_number,'') AS class_id, IFNULL(t2.del, 1) AS DISABLE, t2.loginName1 AS loginName, IFNULL(c.class_number,'') AS class_number, o.`name` org_name, c.start_year, c.dict_code section_code, d.`name` section_name, o. CODE AS orgCode, o.org_type AS orgType, CONCAT( SUBSTRING(c.class_number, 6, 2), '班' ) AS class_name, t1.sex, t1.realname, sg.grade_dict grade_code, dd. NAME AS grade_name FROM person t1 inner JOIN org o ON o.id = t1.org_id LEFT JOIN login t2 ON t1.login_id = t2.id LEFT JOIN class c ON t1.org_id = c.org_id and t1.class_number = c.class_number and c.del=0 LEFT JOIN dict d ON d. CODE = c.dict_code LEFT JOIN section_grade AS sg ON c.dict_code = sg.section_dict AND #{schoolYear} - c.start_year = sg.sort LEFT JOIN dict dd ON dd.`code` = sg.grade_dict where
t1.del = 0 AND t1.login_id=#{loginId} AND o.code = #{orgCode} AND t1.org_id=#{orgId} AND t1.person_type=#{userType} AND t1.modifydatetime > #{modifyTateTime} AND t1.realname like CONCAT('%',#{realName},'%') AND t1.class_number =#{classNumber} AND t1.sex =#{sex}
AND t1.org_id IN
#{item}
limit #{start} , #{pageSize}

