mapper.delete:
update person set del = '1' ,modifydatetime = #{modifydatetime}
where


        id = #{id}


        id in

            '${item}'

mapper.select:
select
t1.*,
IFNULL(t2.del,1) as disable,
t2.loginName1 as loginName,
t3.name as orgName,
t3.code as orgCode,
t3.org_type as orgType
from person t1 inner join org t3
on t1.org_id = t3.id and t1.del = 0
 AND t1.login_id=#{loginId}
 AND t3.code = #{orgCode}
 AND t1.org_id=#{orgId}
AND t1.modifydatetime > #{modifyTateTime}
 AND t1.person_type=#{userType}
 AND t1.sex=#{sex}
AND t1.realname like CONCAT('%',#{realName},'%')

    AND t1.org_id IN

        #{item}


left join login t2 on t1.login_id = t2.id

    limit #{start} , #{pageSize}

SELECT GROUP_CONCAT(t3.name) departmentName,GROUP_CONCAT(t3.id) departmentId, t2.*,'1' as isDepartment,t4.loginName1 as loginname FROM department_user t1 LEFT JOIN person t2 ON t1.uid = t2.id LEFT JOIN org t3 on t1.department_id = t3.id LEFT JOIN login t4 ON t2.login_id = t4.id WHERE
t1.department_id = #{departmentId}
t1.department_id in
'${item}'
GROUP BY t2.id
limit #{start} , #{pageSize}

SELECT t1.*, t2.loginName1 AS loginName FROM person t1 LEFT JOIN login t2 ON t1.login_id = t2.id AND t1.person_type = 'PT_STUDENT' AND t1.del = 0 AND t1.org_id = #{schoolId}
and t1.class_number = #{classNumber}
and t1.class_number in
'${item}'
AND t1.realname like CONCAT('%',#{studentName},'%')
WHERE t2.id IS NOT NULL
